import { useState } from "react"
import { useWorkoutsContext } from "../hooks/useWorkoutsContext"
import { useAuthContext } from '../hooks/useAuthContext'


const InfluForm = () => {

  const { dispatch } = useWorkoutsContext()
  const { user } = useAuthContext()

  const [influName, setInfluName] = useState('')
  const [influFollowerCount, setInfluFollowerCount] = useState('')
  const [error, setError] = useState(null)
  const [emptyFields, setEmptyFields] = useState([])


  const handleSubmit = async (e) => {
    e.preventDefault()

    if (!user) {
      setError('You must be logged in')
      return
    }

    const influencer = {influName, influFollowerCount}

    const response = await fetch('https://torleado.herokuapp.com/api/influencers', {
        method: 'POST',
        body: JSON.stringify(influencer),
        headers: {
          'Content-Type': 'application/json',
          'Authorization': `Bearer ${user.token}`
        }
      })
      const json = await response.json()

      if (!response.ok) {
        setError(json.error)
        setEmptyFields(json.emptyFields)
      }
      if (response.ok) {
       setInfluName('')
       setInfluFollowerCount('')
        setError(null)
        setEmptyFields([])
        dispatch({type: 'CREATE_WORKOUT', payload: json})
      }
    }

    return(
       <div>
        <h1>FORM</h1>
        <form>
            <label>Name</label>
            <input>
            </input>

            <label>Follower Count</label>
            <input>
            </input>
            <button>SAVE</button>
        </form>
       </div> 
    )
}

export default InfluForm
